import {
    FETCH_BOOKS_START,
    FETCH_BOOKS_SUCCESS,
    FETCH_BOOKS_FAILURE
} from 'actionTypes'

export const fetchBooks = () => async dispatch =>{
    dispatch({type: FETCH_BOOKS_START})
try {
    const books = await fetchBooksApi()
    dispatch({type: FETCH_BOOKS_SUCCESS,payload: books})
}
catch (err) {
    dispatch({type: FETCH_BOOKS_FAILURE,
    payload: err,
error: true})
}
}